/**
 * Fetches doctors by specialty ID from the backend API
 * 
 * @param specialtyId - The ID of the specialty to filter doctors by
 * @returns Promise<Doctor[]> - Array of doctors for the specified specialty
 * @throws ApiError - When the API request fails or returns an error
 * 
 * OpenAPI Specification:
 * GET /api/v1/doctors?specialty_id={specialtyId}
 * 
 * Success Response (200):
 * {
 *   "message": "Doctors retrieved successfully",
 *   "data": Doctor[]
 * }
 * 
 * Error Response (404):
 * {
 *   "error": "Not Found",
 *   "message": "No doctors found for the specified specialty"
 * }
 */
export async function fetchDoctorsBySpecialty(specialtyId) {
  // Validate input
  if (!specialtyId || specialtyId <= 0) {
    throw new ApiError('Invalid specialty ID. Must be a positive number.', 400);
  }
  const url = `${API_BASE_URL}/doctors?specialty_id=${specialtyId}`;
  try {
    const response = await fetch(url, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        // Add authorization header if token exists
        ...(getAuthToken() && {
          'Authorization': `Bearer ${getAuthToken()}`
        })
      }
    });

    // Handle different HTTP status codes
    if (!response.ok) {
      const errorData = await response.json().catch(() => ({
        error: 'Unknown Error',
        message: `HTTP ${response.status}: ${response.statusText}`
      }));

      // Handle specific 404 case
      if (response.status === 404) {
        throw new ApiError(`No doctors found for specialty ID ${specialtyId}`, 404, errorData);
      }

      // Handle other error status codes
      throw new ApiError(errorData.message || `Request failed with status ${response.status}`, response.status, errorData);
    }

    // Parse successful response
    const apiResponse = await response.json();

    // Validate response structure
    if (!apiResponse.data || !Array.isArray(apiResponse.data)) {
      throw new ApiError('Invalid response format from server', 500);
    }
    return apiResponse.data;
  } catch (error) {
    // Re-throw ApiError instances
    if (error instanceof ApiError) {
      throw error;
    }

    // Handle network errors and other exceptions
    if (error instanceof TypeError && error.message.includes('fetch')) {
      throw new ApiError('Network error: Unable to connect to the server. Please check your internet connection.', 0);
    }

    // Handle JSON parsing errors
    if (error instanceof SyntaxError) {
      throw new ApiError('Server returned invalid response format', 500);
    }

    // Handle unknown errors
    throw new ApiError(`Unexpected error: ${error instanceof Error ? error.message : 'Unknown error occurred'}`, 500);
  }
}

