// API Response Types
export interface ApiResponse<T> {
  message: string;
  data: T;
}

export interface ErrorResponse {
  error: string;
  message: string;
}

// Doctor and Specialty Types
export interface Specialty {
  id: number;
  name: string;
  description: string;
  is_active: boolean;
  created_at: string;
  updated_at: string;
}

export interface Doctor {
  id: number;
  name: string;
  specialty_id: number;
  is_active: boolean;
  created_at: string;
  updated_at: string;
  specialty?: Specialty;
}

// Data for creating a new doctor
export interface DoctorData {
  name: string;
  specialty_id: number;
  is_active?: boolean; // Optional, defaults to true
}

// API Client Error Types
export class ApiError extends Error {
  constructor(
    message: string,
    public status: number,
    public response?: ErrorResponse
  ) {
    super(message);
    this.name = 'ApiError';
  }
}

// Appointment Booking Types
export interface BookingData {
  doctor_id: number;
  patient_id: number;
  appointment_time: string; // ISO 8601 format
  notes?: string;
}

export interface BookingResponse {
  appointment_id: number;
  doctor_id: number;
  patient_id: number;
  appointment_time: string;
  status: string;
  notes?: string;
  created_at: string;
  message: string;
}

export interface AppointmentStatus {
  id: number;
  status: 'scheduled' | 'confirmed' | 'cancelled' | 'completed';
  appointment_time: string;
  doctor_name?: string;
  patient_name?: string;
}

// API Configuration
export const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:8080/api/v1';
