import { useState, useEffect } from 'react';
import apiClient from '../utils/apiClient';
import { Appointment, ApiResponse } from '../types/api';
import { useAuth } from '../contexts/AuthContext';

export interface UsePatientAppointmentsResult {
  appointments: Appointment[];
  loading: boolean;
  error: string | null;
  refetch: () => Promise<void>;
}

/**
 * Custom hook to fetch and manage patient appointments
 * Fetches appointments from GET /api/v1/appointments/patient
 */
export const usePatientAppointments = (): UsePatientAppointmentsResult => {
  const [appointments, setAppointments] = useState<Appointment[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const { authState } = useAuth();
  const { user } = authState;

  const fetchAppointments = async () => {
    if (!user) {
      setAppointments([]);
      setLoading(false);
      return;
    }

    try {
      setLoading(true);
      setError(null);

      const response = await apiClient.get<ApiResponse<Appointment[]>>('/api/v1/appointments/patient');

      if (response.data.success && response.data.data) {
        setAppointments(response.data.data);
      } else {
        setError(response.data.message || 'Failed to fetch appointments');
      }
    } catch (err: any) {
      console.error('Error fetching patient appointments:', err);
      setError(err.response?.data?.message || 'Failed to fetch appointments');
    } finally {
      setLoading(false);
    }
  };

  const refetch = async () => {
    await fetchAppointments();
  };

  useEffect(() => {
    fetchAppointments();
  }, [user]);

  return {
    appointments,
    loading,
    error,
    refetch
  };
};
