// API Response Types
export interface ApiResponse<T> {
  message: string;
  data: T;
}

export interface ErrorResponse {
  error: string;
  message: string;
}

// Doctor and Specialty Types
export interface Specialty {
  id: number;
  name: string;
  description: string;
  is_active: boolean;
  created_at: string;
  updated_at: string;
}

export interface Doctor {
  id: number;
  name: string;
  specialty_id: number;
  is_active: boolean;
  created_at: string;
  updated_at: string;
  specialty?: Specialty;
}

// API Client Error Types
export class ApiError extends Error {
  constructor(
    message: string,
    public status: number,
    public response?: ErrorResponse
  ) {
    super(message);
    this.name = 'ApiError';
  }
}

// API Configuration
export const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:8080/api/v1';
